import { Component, Input, ChangeDetectorRef } from '@angular/core';

@Component({
  selector: 'app-simulation-results',
  templateUrl: './simulation-results.component.html',
  styleUrls: ['./simulation-results.component.css']
})
export class SimulationResultsComponent {
  @Input() totalSimulations: number = 0;
  @Input() wins: number = 0;
  @Input() losses: number = 0;


  constructor(private cdr: ChangeDetectorRef) { }

  // In a method where you set the simulationResults
  updateSimulationResults(result: any) {
    this.totalSimulations = result.totalSimulations;
    this.wins = result.wins;
    this.losses = result.losses;
    this.cdr.detectChanges(); // Trigger change detection
  }

}
